using GameJamProject.UI;using UnityEngine;namespace UnityRoomProject.UI{    public class TitleUI : MonoBehaviour    {        public Node TitleScene; // RootNodeとして設定されるノード        private void Start()        {            if (TitleScene == null) return;            TitleScene.OnInitialize();            UIManager.Instance.SetRootNode(TitleScene);        }        public void OpenHomeWindow()        {            Debug.Log("Open HomeWindow");            var homeWindow = TitleScene.ChildNodes.Find(node => node.NodeName == "HomeWindow");            if (homeWindow != null)            {                UIManager.Instance.OpenNode(homeWindow);            }        }                public void CloseHomeWindow()        {            Debug.Log("Close HomeWindow");            var homeWindow = TitleScene.ChildNodes.Find(node => node.NodeName == "HomeWindow");            if (homeWindow != null)            {                UIManager.Instance.CloseNode(homeWindow);            }        }    }}